import { Injectable } from '@angular/core';
import * as L from 'leaflet';

@Injectable({
  providedIn: 'root'
})
export class MarkerService {

  masterClass = 'modify-marker';
  goodClass = 'good';
  errorClass = 'error';
  undefinedClass = 'undefined';
  selectedClass = 'selected';
  normalTypeClass = 'normal';
  ptzTypeClass = 'ptz';
  
  constructor() { }

  // maker service

  getIcon(status: any, type:any, isundefined?: any, isselected?: any, vov?: any){
    if(vov){
      status = 'new';
    }
      var className = this.masterClass + ' ' + status + ' ' + type;
      className = (isundefined) ? className + ' ' + this.undefinedClass: className;
      className = (isselected) ? className + ' ' + this.selectedClass: className;
      var size = (status ===this.errorClass) ? 24 : 32;
      return L.divIcon({
  
        className: className,
        iconSize: [size, size],
        iconAnchor: [size / 2, size / 2],
        html: '<div class="marker-background"></div>' +
              '<div class="marker-icon"></div>' +
              '<div class="marker-content"></div>'
      });
  }

  changeStatusTogoodClass(icon:any){
    var size = 32;
    icon.className = icon.className.replace(this.errorClass,this.goodClass);
    icon.iconSize = [size,size];
    icon.iconAnchor = [size / 2, size / 2];
    return icon;
  }

  changeStatusToerrorClass(icon:any){
    var size = 24;
    icon.className = icon.className.replace(this.goodClass, this.errorClass);
    icon.iconSize = [size, size];
    icon.iconAnchor = [size / 2, size / 2];
    return icon;
  }

  changeTypeToNormal(icon:any){
    if(icon.className.indexOf(this.ptzTypeClass)>-1){
      icon.className = icon.className.replace(this.ptzTypeClass, this.normalTypeClass);
    } else if(icon.className.indexOf(this.normalTypeClass)<0){
      icon.className = icon.className + ' ' +  this.normalTypeClass;
    }
    return icon;
  }

  changeTypeToPTZ(icon:any){
  if (icon.className.indexOf(this.normalTypeClass) > -1) {
      icon.className = icon.className.replace(this.normalTypeClass, this.ptzTypeClass);
  } else if (icon.className.indexOf(this.ptzTypeClass) < 0) {
      icon.className = icon.className + ' ' + this.ptzTypeClass;
  }
  return icon;
  }

  changeTypeToUnknown(icon:any){
    icon.className = icon.className.replace(' ' + this.normalTypeClass, '');
    icon.className = icon.className.replace(' ' + this.ptzTypeClass, '');
    return icon;
  }

  undefinedMarker(icon:any){
    if (icon.className.indexOf(this.undefinedClass) < 0) {
      icon.className = icon.className + ' ' + this.undefinedClass;
  }
  return icon;
  }

  noUndefinedMarker(icon:any){
    icon.className = icon.className.replace(' ' + this.undefinedClass, '');
    return icon;
  }

  selectedMarker(icon:any){

    if (icon.className.indexOf(this.selectedClass) < 0) {
      icon.className = icon.className + ' ' + this.selectedClass;
    }
    return icon;
  }

  deSelectedMarker(icon:any){
    icon.className = icon.className.replace(' ' + this.selectedClass, '');
    return icon;
  }

  // event maker
  jamIcon:any = {
    normal: {
      html: '<div class="marker-jam-normal"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5]
    },
    congestion: {
      html: '<div class="marker-jam-moderate"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5],
    },
    incident: {
      html: '<div class="marker-jam-incident"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5]
    },
    jam: {
      html: '<div class="marker-jam-extreme"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5]
    },
    flood: {
      html: '<div class="marker-jam-flood"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5]
    },
    default: {
      html: '<div class="marker-jam-default"></div>',
      iconSize: null,
      iconAnchor: [24.75, 54.75],
      popupAnchor: [0, -52.5]
    }
  };

  eventMaker(type:any){
    return this.jamIcon[type];
  }

  // road event style

  markerClass = 'marker-road-event';
  labelClass:any = {
      marker: 'extend-marker-road-event',
      image: 'extend-image-road-event',
      label: 'extend-label-road-event'
  };
  listColor: any = {
      red: '#db2828',
      orange: '#f26202',
      yellow: '#eaae00',
      green: '#21ba45',
      teal: '#009c95',
      blue: '#2185d0',
      purple: '#a333c8',
      pink: '#e61a8d',
      brown: '#975b33',
      black: '#1b1c1d'
  };

  listIcons = [{
    _id: 1,
    name: 'Camera',
    data: {
        iconUrl: 'data:image/png;base64,',
        iconSize: [33.5, 40],
        iconAnchor: [16.75, 40],
        popupAnchor: [-3, -76]
    }
},
{
    _id: 2,
    name: 'Bãi đỗ xe',
    data: {
        iconUrl: 'data:image/png;base64,',
        iconSize: [33.5, 40],
        iconAnchor: [16.75, 40],
        popupAnchor: [-3, -76]
    }
},
{
    _id: 3,
    name: 'Công trường',
    data: {
        iconUrl: 'data:image/png;base64,',
        iconSize: [33.5, 40],
        iconAnchor: [16.75, 40],
        popupAnchor: [-3, -76]
    }
},
{
    _id: 4,
    name: 'Bãi đỗ xe',
    data: {
        iconUrl: 'data:image/png;base64,',
        iconSize: [24, 24],
        iconAnchor: [12, 12],
        popupAnchor: [-3, -76]
    }
  }];

  getListIcons(){
    return this.listIcons;
  }

  getListColors(){
    return this.listColor;
  }

  getColor(color:any){
    return this.listColor[color]
  }

  geticon(type:string, color:any, title?:any, image?:any, radius?:any){
    var html = ""
    if (image) {
      html += '<img class="image-marker" src="' + image + '"/>';
    } 
    if (title) {
      html += '<p class="' + this.labelClass[type] + '">' + title + '</p>';
    }
    
    return L.divIcon({
      className: this.markerClass + ' ' + color || '',
      iconSize: type === 'label' ? [0, 0] : [radius || 10, radius || 10],
      html: html
    })
  }

  getIconForDirection(markerdirection:any,color:any){
    return {
      className: 'marker-arrow' + markerdirection + ' ' + color,
      iconSize: [0, 0]
    };
  }

  getPathStyle(type:any, color:any){
    var geoColor = this.listColor[color] || color;
    var style: any = {
      label: {
        fillColor: geoColor,
        weight: 2,
        opacity: 0.9,
        color: geoColor,
        fillOpacity: 0.7
      },
      geo: {
        fillColor: geoColor,
        weight: 6,
        opacity: 1,
        color: geoColor,
        fillOpacity: 0.7
      },
      circle: {
        fill: true,
        weight: 5,
        opacity: 1,
        color: geoColor,
        fillColor: geoColor,
        fillOpacity: 1
      },
      custom: {
        fillColor: geoColor,
        weight: 6,
        opacity: 0.9,
        color: geoColor,
        fillOpacity: 0.5
      },
      hover: {
        fillColor: geoColor,
        weight: 9,
        opacity: 0.3,
        color: geoColor,
        fillOpacity: 0.7
      }
    };
    return style[type];
  }

  createFeature(type:any, latLng:any){
    latLng = latLng || latLng(0,0);
    var sampleData:any = {
              marker: {
                  'type': 'Feature',
                  'properties': {
                      'type': 'marker',
                      'color': 'black',
                      'title': 'Chú thích'
                  },
                  'geometry': {
                      'type': 'Point',
                      'coordinates': [
                          latLng.lng,
                          latLng.lat
                      ]
                  }
              },
              label: {
                  'type': 'Feature',
                  'properties': {
                      'type': 'label',
                      'color': 'black',
                      'title': 'Chú thích'
                  },
                  'geometry': {
                      'type': 'GeometryCollection',
                      'coordinates': [],
                      'geometries': [{
                              'type': 'Point',
                              'coordinates': [
                                  latLng.lng,
                                  latLng.lat
                              ],
                              'properties': {
                                  'type': 'marker'
                              }
                          },
                          {
                              'type': 'Point',
                              'coordinates': [
                                  latLng.lng + 0.002,
                                  latLng.lat + 0.002
                              ],
                              'properties': {
                                  'type': 'label',
                              }
                          }
                      ]
                  }

              },
              geo: {
                  'type': 'Feature',
                  'properties': {
                      'type': 'geo',
                      'color': 'black',
                      'title': 'Chú thích',
                      'totalLength': 0
                  },
                  'geometry': {
                      'type': 'GeometryCollection',
                      'coordinates': [],
                      'geometries': []
                  }
              },
              custom: {
                  'type': 'Feature',
                  'properties': {
                      'type': 'custom',
                      'color': 'black',
                      'title': 'Chú thích'
                  },
                  'geometry': {
                      'type': 'GeometryCollection',
                      'coordinates': [],
                      'geometries': []
                  }
              },
              image: {
                  'type': 'Feature',
                  'properties': {
                      'type': 'image',
                      'title': 'Chú thích',
                      'radius': 30,
                      'image': {
                          'id': '101',
                          'name': 'Đường cấm',
                          'url': '../images/bienbao/101.png'
                      }
                  },
                  'geometry': {
                      'type': 'Point',
                      'coordinates': [
                          latLng.lng,
                          latLng.lat
                      ]
                  }
              }
          };
          return sampleData[type];
        }
}
